INSERT INTO `t_person_notes` VALUES (5, '整理内容，提交码云', '提交了前后端代码，及一些框架搭建配置。', '2021-01-03 11:08:04', '13px Small', '#882222', 'el-icon-s-data', '');
INSERT INTO `t_person_notes` VALUES (6, '加入wangEditor富文本编辑器', '加入wangEditor富文本编辑器，添加父组件，直接引入import EditorBar from \'../components/wangEnduit\'，声明组件 components: { EditorBar },即可使用，具体查看notes.vue', '2021-01-03 14:19:16', '13px Small', '#9C2E2E', '', '');
INSERT INTO `t_person_notes` VALUES (7, '加入echarts', 'main.js 已添加var echarts = require(\'echarts\');  Vue.prototype.$echarts = echarts 引入组件，具体使用 let echarts = require(\'echarts/lib/echarts\')，初始化 this.$echarts.init(document.getElementById(\'main\'))使用即可', '2021-01-09 11:41:28', '12px Extra Small', '#992828', 'el-icon-s-data', '');
INSERT INTO `t_person_notes` VALUES (8, '测试', '<h1 id=\"0asnt\">测试上到几<i><font face=\"华文仿宋\">点时间决定讲究的是&nbsp; 撒<span style=\"background-color: rgb(139, 170, 74);\">大声</span>地&nbsp;</font></i></h1>', '2021-01-01 12:12:51', '13px Small', '#FF0000', '', '');
INSERT INTO `t_person_notes` VALUES (9, '11111', '<p>122121<img src=\"http://otp.cdinfotech.top/uploads/workspace/2021/1/9/52139f901ab04a5fa3c4bac8640876e7.png\" style=\"max-width:100%;\"><br></p>', '2021-01-01 00:00:00', '13px Small', '', '1', '');
INSERT INTO `t_person_notes` VALUES (10, '添加修改密码的功能', '<p>点击右上角用户名，可以修改密码，输入原密码，新密码即可。</p>', '2021-01-11 09:43:19', '13px Small', '#A22B2B', 'el-icon-s-goods', '');
INSERT INTO `t_person_notes` VALUES (11, '根据用户角色登录显示左侧菜单', '<p>根据用户角色登录显示左侧菜单</p>', '2021-01-11 17:23:42', '13px Small', '#E41010', ' el-icon-warning', '');
INSERT INTO `t_person_notes` VALUES (12, '增加登录日志', '<p>增加登录日志，记录ip、时间、登录人</p>', '2021-01-13 10:57:34', '12px Extra Small', '#CC1212', 'el-icon-date', '');
INSERT INTO `t_person_notes` VALUES (13, '后台增加了定时任务和redis', '<p>一、Spring&nbsp;<span style=\"text-align: initial;\">Scheduling定时任务</span></p><p><span style=\"text-align: initial;\">二、redis 定义redisConfig、redisUtil&nbsp;</span></p><p><span style=\"text-align: initial;\">三、完善了jwt和SpringSecurity的登录，修改 盐值密码为 SpringSecurity加密</span></p><p><span style=\"text-align: initial;\">passwordEncoder.encode()</span></p>', '2021-01-17 20:47:09', '12px Extra Small', '#B00707', 'el-icon-minus', '');
INSERT INTO `t_person_notes` VALUES (14, '重大更新', '<p>1、首页页面，文章无限滚动查看列表，热门文章、<span style=\"font-size: 1em; text-align: initial;\">留言板功能</span></p><p>2、<span style=\"font-size: 1em; text-align: initial;\">查看文章详情，点赞，评论功能</span></p><p>4、后台redis定时任务</p>', '2021-01-24 12:34:12', '12px Extra Small', '#FF0000', 'el-icon-message-solid', '');
INSERT INTO `t_person_notes` VALUES (15, '新增聊天室、mavon-editor - MD文档编辑器', '<p>新增聊天室、mavon-editor - MD文档编辑器</p>', '2021-01-28 10:25:39', '13px Small', '#A22F2F', '', '');
