DROP TABLE IF EXISTS `t_ariticle`;
CREATE TABLE `t_ariticle`  (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `title` varchar(50) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NULL DEFAULT NULL COMMENT '标题',
  `summary` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NULL DEFAULT NULL COMMENT '文章摘要',
  `content` longtext CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NULL COMMENT '文章内容',
  `author` varchar(50) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NULL DEFAULT NULL COMMENT '作者',
  `browse_num` int(11) NOT NULL DEFAULT 0 COMMENT '浏览数',
  `like_num` int(11) NOT NULL DEFAULT 0 COMMENT '点赞数',
  `comment_num` int(11) NOT NULL DEFAULT 0 COMMENT '评论数',
  `create_time` datetime(0) NULL DEFAULT NULL COMMENT '创建时间',
  `update_time` datetime(0) NULL DEFAULT NULL COMMENT '更新时间',
  `publish_time` datetime(0) NULL DEFAULT NULL COMMENT '发布时间',
  `is_del` int(1) NOT NULL DEFAULT 0 COMMENT '删除状态 0->未删除 1->已删除',
  `is_publish` int(1) NULL DEFAULT NULL COMMENT '发布状态 0->未发布 1->已发布',
  `type` int(1) NULL DEFAULT NULL COMMENT '类型 0->转载 1->原文 2->翻译',
  `original_link` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NULL DEFAULT NULL COMMENT '原文链接',
  `user_id` int(11) NULL DEFAULT NULL COMMENT '用户id',
  PRIMARY KEY (`id`) USING BTREE
) ENGINE = InnoDB AUTO_INCREMENT = 47 CHARACTER SET = utf8mb4 COLLATE = utf8mb4_general_ci COMMENT = '文章表' ROW_FORMAT = Dynamic;

